,#{user_type}
,#{name}
,#{password}
,#{tel}
,#{sex}
,STR_TO_DATE(#{birth}, '%Y%m%d')
,#{mail}
,#{addr}
,now()
,#{pid}
,#{std_numsel}
,#{comp_name}
,#{comp_addr}
,#{comp_tel}
,#{comp_mail}
